    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 p-4">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded-lg p-6 relative">
        {token && (
          <button
            onClick={handleLogout}
            className="absolute top-4 right-4 text-sm text-red-500 hover:text-red-700"
          >
            üö™ Wyloguj siƒô
          </button>
        )}

        <h1 className="text-4xl font-extrabold text-center text-blue-700 mb-10 tracking-tight">
          üõçÔ∏è Webshop2
        </h1>

        {!token ? (
          <Auth />
        ) : (
          <>
            <AddProduct />
            <hr className="my-6" />
            <ProductList />
          </>
        )}
      </div>
    </div>
  );
}

export default App;
