import { addProduct } from '../api';

export const handleAddProduct = async (e, title, description, imageUrl, setTitle, setDescription, setImageUrl) => {
  e.preventDefault();
  const token = localStorage.getItem('token');
  if (!token) {
    alert("Zaloguj się, aby dodać produkt.");
    return;
  }

  const response = await addProduct({ title, description, image_url: imageUrl }, token);

  if (response.ok) {
    alert('Dodano!');
    setTitle('');
    setDescription('');
    setImageUrl('');
  } else {
    alert('Błąd dodawania.');
  }
};
